import serialimport timeimport numpy as np# 设置串口名称（例如 COM3 或 /dev/ttyACM0）# 根据你的操作系统和硬件选择正确的串口port = 'COM4'  # Windows# port = '/dev/ttyACM0'  # Linux with USB-serial converterrawdata = []# 设置波特率baudrate = 9600# 打开串口ser = serial.Serial(port, baudrate)print("连接到串口: " + port)Sample_Time = 10start_time = time.time()try:    while time.time()-start_time < Sample_Time:        # 读取串口数据        data = ser.readline()        if data:            # 打印读取到的数据            print(data)            rawdata.append(data)except KeyboardInterrupt:    print("关闭串口连接")    ser.close()print(rawdata)filename = "testdata.txt"np.savetxt('array.txt', rawdata, fmt='%s')