from pdf2image import convert_from_pathfrom PIL import Imagepdf_path = 'ykw'  # PDF文件的路径images = convert_from_path(pdf_path+'.pdf', dpi=200)  # dpi参数决定图片的清晰度for i, image in enumerate(images):    image.save(f'{pdf_path}.png', 'PNG')  # 保存图片image_path = pdf_path + '.png'image = Image.open(image_path)cropped_image = image.crop((150, 900, 2070, 1125))cropped_image.save(f"cropped_image_{pdf_path}.jpg")