import timefrom loguru import logger#import Recognizesystemimport FeatureExtractorimport ModelTrainnerimport osimport tkinter as tkfrom tkinter import simpledialog, messageboxdef get_filenames_using_os(folder_path):    filenames = os.listdir(folder_path)    return filenamesdef DataProcess():    namelist = get_filenames_using_os(os.getcwd() + '\data')    for name in namelist:        if name[0:4] != 'tesr':            FeatureExtractor.main(name)            print(f'Subject {name} data processed.')            print('------------------------------------')    print('-------------------------------------------------------')    print('All Exist Data Extracted.')    print('-------------------------------------------------------')# 创建主界面root = tk.Tk()root.title("Main Interface")# 创建Label用于显示结果result_var = tk.StringVar()result_label = tk.Label(root, textvariable=result_var)def open_secondary_interface():    username = simpledialog.askstring("Input", "Please enter the username:")    if username:        result = ModelTrainner.main(username)        result_var.set(result)  # 更新Label的文本内容为方法的返回值# 创建按钮并绑定到函数register_button = tk.Button(root, text="Register", command=lambda: messagebox.showinfo("Result", DataProcess()))recognize_button = tk.Button(root, text="Recognize", command=open_secondary_interface)# 布局按钮和Labelregister_button.pack(pady=10)recognize_button.pack(pady=10)result_label.pack(pady=10)# 运行主循环root.mainloop()#DataProcess()#print(ModelTrainner.main('ykw'))